      const userInfoBox = await row.findElement(By.class("user-info-box"));
      if (userInfoBox === undefined) {
        continue;
      }
      assert.Equal(userInfoBox.getText(), `Sally: ${moods[i]}Jun 17, 9:48 AM`);
      i += 1;
    }
  });
